import React,{useState} from "react";
var string="";
function App()
{
const [display,setdisplay]=useState("");

function handleChange(event)
{
    
string+=event.target.value;
    setdisplay(string);
}



function handleresult(event)
{
    var audio=new Audio("/sounds/wrong.mp3");
audio.play();
let answer=eval(string);
setdisplay(answer);
}

function handleC()
{
    string="";
    setdisplay("");
}

return (
<div class="box">
<h1 class="heading">React Calculator</h1>
<input name="expression" value={display}></input><button onClick={handleC} >C</button>
<div class="first-row">
<button class="rectangle"  onClick={handleChange} value="1">1</button>
<button class="rectangle" onClick={handleChange} value="2">2</button>
<button class="rectangle" onClick={handleChange} value="3">3</button>
<button class="square" onClick={handleChange} value="/">/</button>
</div>
<div class="second-row">
<button class="rectangle" onClick={handleChange} value="4">4</button>
<button class="rectangle" onClick={handleChange} value="5">5</button>
<button class="rectangle" onClick={handleChange} value="6">6</button>
<button class="square" onClick={handleChange} value="-">-</button>
</div>
<div class="third-row">
<button class="rectangle" onClick={handleChange} value="7">7</button>
<button class="rectangle" onClick={handleChange} value="8">8</button>
<button class="rectangle" onClick={handleChange} value="9">9</button>
<button class="square" onClick={handleChange} value="+">+</button>
</div>
<div class="fourth-row">
<button class="rectangle" onClick={handleChange} value=".">.</button>
<button class="rectangle" onClick={handleChange} value="0">0</button>
<button class="rectangle" onClick={handleresult} value="=">=</button>
<button class="square" onClick={handleChange} value="*">*</button>
</div>

</div>
);
}


export default App;
